module.exports = function Fibonacci (stopsAt) {
  this.from = 0;
  this.to = stopsAt;
  this.sequence = [];
}

Fibonacci.prototype.getSequence = function () {
  var self = this;
  
  while (self.from < self.to) {
    var toAdd = ((self.from == 0) ? 1 : self.sequence[self.sequence.length - 1])
      , lastAdded = self.sequence[self.sequence.length - 2] || 0;
    
    self.sequence.push(toAdd + lastAdded)
    self.from++;
  }
  return self.sequence;
}