var grid = require('./grid');

module.exports = {
  grid: function (width, columns, gutter, padding) {
    var gridInstance = new grid(width || 100, columns || 12, gutter || [0], padding || [0]);

    return {
      cols: gridInstance.getColumns(),
      gutter: gridInstance.gutter.join(' '),
      padding: gridInstance.padding.join(' '),
      floats: gridInstance.getFloats(),
      subtractGutter: gridInstance.calcGutter(gutter || [0]),
      subtractPadding: gridInstance.calcGutter(padding || [0])
    }
  }
}