var _typography
  , calculateTypography
  , getFontSize
  , getHeadings
  , getLineHeight
  , getParagraph
  , setLineHeightCalculation; 

// constructor object
_typography = module.exports = function Typography (fontSize, lineHeight) {
  this.fontSize = fontSize || [1];
  this.lineHeight = lineHeight || 1.5;
}
// the function wich will calculate the font-size, line-height and margin
calculateTypography = function (typoArray, boolAddMargin) {
  var self = this;

  typoArray = typoArray.map(function (value, offset) {
    var lineHeight = self.lineHeight / value
      , margin = 0;

    if (boolAddMargin) {
      margin = lineHeight - value;
    }

    return {
      fontSize: value,
      lineHeight: lineHeight,
      margin: margin
    }
  });

  return typoArray; 
}

getFontSize = function () {
  return this.fontSize;
}

getHeadings = function (boolAddMargin) {
  return this.calculateTypography(this.fontSize, boolAddMargin);
}

getLineHeight = function () {
  return this.lineHeight;
}

getParagraph = function () {
  return this.calculateTypography(this.fontSize, true);
}

